import "./App.css";
import Question from "./components/Question";
import Answers from "./components/Answers";
import ScoreBoard from "./components/ScoreBoard";
import { Header } from "semantic-ui-react";

function App() {
  return (
    <div className="App">
      <Header as="h1">Subdivision App</Header>
      <ScoreBoard />
      <Question />
      <Answers />
