import React from "react";
import { connect } from "react-redux";
import { shuffle } from "lodash";
import uuid from "uuid";
import {
  handleCorrectAnswer,
  handleWrongAnswer
} from "../actions/handleAnswers";
import { handlePlayNext } from "../actions/handlePlayNext";
import { Button } from "semantic-ui-react";
import Abcjs from "react-abc";

const Answers = ({
  questions,
  handleCorrectAnswer,
  handleWrongAnswer,
  handlePlayNext
}) => {
  const generateBtns = () =>
    shuffle([
      <Button
        key={uuid()}
        onClick={() => {
          handleCorrectAnswer();
          handlePlayNext();
        }}
      >
        <img src={questions["correctAnswer"]} />
      </Button>,
      <Button
        key={uuid()}
        onClick={() => {
          handleWrongAnswer();
          handlePlayNext();
        }}
      >
        <img src={questions["wrongAnswer"]} />
      </Button>
    ]);
  return (
    <div className="answers">
      <div className="answer-btns">{generateBtns().map(button => button)}</div>
    </div>
  );
};

const mapStateToProps = state => ({
  questions: state.questions
});

export default connect(mapStateToProps, {
  handleCorrectAnswer,
  handleWrongAnswer,
  handlePlayNext
})(Answers);
