import React from "react";
import { connect } from "react-redux";
import { shuffle } from "lodash";
import uuid from "uuid";
import {
  handleCorrectAnswer,
  handleWrongAnswer
} from "../actions/handleAnswers";
import { Button } from "semantic-ui-react";

const Answers = ({
  questions,
  handleCorrectAnswer,
  handleWrongAnswer,
  handlePlayNext
}) => {
  const generateBtns = () =>
    shuffle([
      //   <Button key={uuid()} onClick={handleCorrectAnswer}>
      <Button key={uuid()} onClick={handlePlayNext}>
        <img src={questions["correctAnswer"]} />
      </Button>,
      <Button key={uuid()} onClick={handleWrongAnswer}>
        <img src={questions["wrongAnswer"]} />
      </Button>
    ]);
  return (
    <div className="answers">
      <div className="answer-btns">{generateBtns().map(button => button)}</div>
    </div>
  );
};

const mapStateToProps = state => ({
  questions: state.questions
});

export default connect(mapStateToProps, {
  handleCorrectAnswer,
  handleWrongAnswer,
  handlePlayNext
})(Answers);

/* 
onImageClick:  dispatch actions "handleCorrectAnswer and playNext"
playNext reducer: has the startStopTimes arry
When it receives 'PLAY_NEXT', it returns the next obj in array
This comes in as state to Questions

