import React from "react";
import { connect } from "react-redux";
import { getRandQuestion } from "../actions/getRandQuestionAction";
import { Button } from "semantic-ui-react";

import loop3 from "../audio/loop3.mp3";

const Question = ({ questions, getRandQuestion, playNext }) => {
  return (
    <div className="question">
      <h1>?</h1>
      <audio
        src={`${loop3}#t=00:00:${playNext.start},00:00:${playNext.stop}`}
        autoPlay
        type="audio/mp3"
      ></audio>

      {/* <Button
        onClick={() => {
          getRandQuestion();
        }}
      >
        Get Random Question
      </Button> */}
    </div>
  );
};

Question.propTypes = {};

const mapStateToProps = state => ({
  questions: state.questions,
  playNext: state.playNext
});

export default connect(mapStateToProps, { getRandQuestion })(Question);
