import { shuffle } from "lodash";
import startStopTimes from "../startStopData";

// array of shuffled objects
const shuffledStartStopTimes = shuffle(startStopTimes);

let count = 0;
export const playNextReducer = (
  //   state = shuffledStartStopTimes[count],
  state = {
    startStopTimes: shuffledStartStopTimes[count],
    wrongNotation: shuffledStartStopTimes[count + 1]
  },
  action
) => {
  switch (action.type) {
    case "PLAY_NEXT":
      count++;
      //   return shuffledStartStopTimes[count];
      return {
        startStopTimes: shuffledStartStopTimes[count],
        wrongNotation: shuffledStartStopTimes[count + 1]
      };
    default:
      return state;
  }
};

/* 
The PROBLEM:
    Each time a loop is played, it will be accompanied
    by the same wrongNotation

    I'd prefer to have this wrongNotation randomized


*/
