import React from "react";
import { connect } from "react-redux";
import { shuffle } from "lodash";
import PropTypes from "prop-types";
import uuid from "uuid";
import {
  handleCorrectAnswer,
  handleWrongAnswer
} from "../actions/handleAnswers";
import { handlePlayNext } from "../actions/handlePlayNext";
import { Button } from "semantic-ui-react";
import Abcjs from "react-abc";

const Answers = ({
  questions,
  handleCorrectAnswer,
  handleWrongAnswer,
  handlePlayNext,
  playNext
}) => {
  const generateBtns = () =>
    shuffle([
      <Button
        className="notation"
        key={uuid()}
        onClick={() => {
          handleCorrectAnswer();
          handlePlayNext();
        }}
      >
        <Abcjs
          className="svg"
          notation={playNext.startStopTimes.notation}
          parserParams={{}}
          // engraverParams={{ responsive: "resize" }}
          renderParams={{ viewportHorizontal: true }}
        />
      </Button>,
      <Button
        key={uuid()}
        onClick={() => {
          handleWrongAnswer();
          handlePlayNext();
        }}
      >
        <Abcjs
          notation={playNext.wrongNotation.notation}
          parserParams={{}}
          renderParams={{ viewportHorizontal: true }}
        />
      </Button>
    ]);
  return (
    <div className="answers">
      <div className="answer-btns">{generateBtns()}</div>
    </div>
  );
};

const mapStateToProps = state => ({
  questions: state.questions,
  playNext: state.playNext
});

export default connect(mapStateToProps, {
  handleCorrectAnswer,
  handleWrongAnswer,
  handlePlayNext
})(Answers);

Answers.propTypes = {
  handleCorrectAnswer: PropTypes.func,
  handleWrongAnswer: PropTypes.func,
  handlePlayNext: PropTypes.func
};

/* 
Start Game, what happends onClick----   startGame === true
We've got "QUESTIONS" which autoPlays audio
Now we've got a shuffled array of objects with start,stop times, and a notation
We need to display 2 DIFFERENT Notations, 1 BEING Correct
*/
