import React from "react";
import { connect } from "react-redux";
import { getRandQuestion } from "../actions/getRandQuestionAction";
import { Button, Popup } from "semantic-ui-react";
import Abcjs from "react-abc";

import loop3 from "../audio/loop3.mp3";

const notations = [
  "X:1\nT:#1\nM:4/4\nL:1/8\nK:perc\n|:cccc cccc|",
  "X:1\nT:#2\nM:4/4\nL:1/8\nK:perc\n|:cc cc/c/ cccc|",
  "X:1\nT:#3\nM:4/4\nL:1/8\nK:perc\n|:c/c c c c/ cc c/c/c/c/|",
  "X:1\nT:#4\nM:4/4\nL:1/8\nK:perc\n|:  c c c c c c/c c/|"
];

const Question = ({ questions, getRandQuestion, playNext }) => {
  return (
    <div className="question">
      <Abcjs
        notation={notations[0]}
        parserParams={{}}
        // engraverParams={{ responsive: "resize" }}
        renderParams={{ viewportHorizontal: true }}
      />
      {/* <Popup
        content="Listen to the accented notes"
        trigger={<img src={questions["correctAnswer"]}></img>}
      /> */}
      <audio
        src={`${loop3}#t=00:00:${playNext.start},00:00:${playNext.stop}`}
        autoPlay
        type="audio/mp3"
      ></audio>

      {/* <Button
        onClick={() => {
          getRandQuestion();
        }}
      >
        Get Random Question
      </Button> */}
    </div>
  );
};

const mapStateToProps = state => ({
  questions: state.questions,
  playNext: state.playNext
});

export default connect(mapStateToProps, { getRandQuestion })(Question);

