import React, { useEffect, useRef } from "react";
import { connect } from "react-redux";
import { getRandQuestion } from "../actions/getRandQuestionAction";
import { Button, Popup } from "semantic-ui-react";
import { shuffle } from "lodash";

// "ref": gives you access to underlying DOM element

import loop3 from "../audio/loop3.mp3";

const startStopTimes = [
  { start: "00.00", stop: "04.85" },
  { start: "05.00", stop: "09.90" },
  { start: "10.10", stop: "15.10" },
  { start: "15.30", stop: "20.30" }
];

const shuffledStartStopTimes = shuffle(startStopTimes);

const Question = ({ questions, getRandQuestion }) => {
  const audioRef = useRef();

  return (
    <div className="question">
      <Popup
        content="Listen to the accented notes"
        trigger={<img src={questions["correctAnswer"]}></img>}
      />
      <audio
        src={`${loop3}#t=00:00:${shuffledStartStopTimes[0].start},00:00:${shuffledStartStopTimes[0].stop}`}
        type="audio/mp3"
        ref={audioRef}
      ></audio>

      <Button
        onClick={() => {
          //   playAudio();
          getRandQuestion();
          console.log(audioRef.current);
          audioRef.current.play();
        }}
      >
        Get Random Question
      </Button>
    </div>
  );
};

const mapStateToProps = state => ({
  questions: state.questions
});

export default connect(mapStateToProps, { getRandQuestion })(Question);
