import React, { useEffect, useRef } from "react";
import { connect } from "react-redux";
import { getRandQuestion } from "../actions/getRandQuestionAction";
import PropTypes from "prop-types";
import loop3 from "../audio/loop3.mp3";

const Question = ({ questions, getRandQuestion, playNext }) => {
  const audio = useRef();
  useEffect(() => {
    // const audio = document.querySelector("audio");
    // audio.play();
    audio.play();
  }, [playNext]);
  return (
    <div className="question">
      <h1>?</h1>
      <audio
        src={`${loop3}#t=00:00:${playNext.start},00:00:${playNext.stop}`}
        // autoPlay
        type="audio/mp3"
        ref="audio"
      ></audio>
    </div>
  );
};

const mapStateToProps = state => {
  console.log(state);
  return {
    questions: state.questions,
    playNext: state.playNext
  };
};

export default connect(mapStateToProps, { getRandQuestion })(Question);

Question.propTypes = {
  playNext: PropTypes.object
};
