import { shuffle } from "lodash";
import startStopTimes from "../startStopData";

const shuffledStartStopTimes = shuffle(startStopTimes);

let count = 0;

export const playNextReducer = (
  state = {
    startStopTimes: shuffledStartStopTimes[count],
    wrongNotation: shuffledStartStopTimes[count + 1]
  },
  action
) => {
  switch (action.type) {
    case "PLAY_NEXT":
      return {
        startStopTimes: shuffledStartStopTimes[(count += 1)],
        wrongNotation: shuffledStartStopTimes[count++]
      };
    default:
      return state;
  }
};
