import notation1 from "./notation/notation1.jpg";
import notation2 from "./notation/notation2.jpg";
// const nums = [1, 2, 3, 4, 5];
const images = [notation1, notation2];

export const randomNumObj = arr => {
  // create empty holding obj
  const result = {};
  //   create a function that returns a random number from the images array
  let randNum = () => Math.ceil(Math.random() * arr.length);
  //   In the "result" obj, add a property called "correctAnswer" and assign it to be the image from the images array at the randNum
  result["correctAnswer"] = images[randNum()];
  //   Get another random Number
  let randNum2 = randNum();
  //   While the image at the "correctAnswer" property in the result object is the same as the image
  while (result["correctAnswer"] === arr[randNum2]) {
    randNum2 = images[randNum()];
  }
  result["wrongAnswer"] = images[randNum2];
  return result;
};

export const questionReducer = (
  state = { correctAnswer: "", wrongAnswer: "" },
  action
) => {
  switch (action.type) {
    case "GET RANDOM QUESTION":
      return randomNumObj(images);
    default:
      return state;
  }
};
