import React from "react";
import { connect } from "react-redux";
import { shuffle } from "lodash";
import uuid from "uuid";
import {
  handleCorrectAnswer,
  handleWrongAnswer
} from "../actions/handleAnswers";
import { handlePlayNext } from "../actions/handlePlayNext";
import { Button } from "semantic-ui-react";
import Abcjs from "react-abc";

const notations = [
  "X:1\nM:4/4\nL:1/8\nK:perc\n|:cccc cccc|",
  "X:1\nM:4/4\nL:1/8\nK:perc\n|:cc cc/c/ cccc|",
  "X:1\nM:4/4\nL:1/8\nK:perc\n|:c/c c c c/ cc c/c/c/c/|",
  "X:1\nM:4/4\nL:1/8\nK:perc\n|:  c c c c c c/c c/|"
];

const Answers = ({
  questions,
  handleCorrectAnswer,
  handleWrongAnswer,
  handlePlayNext
}) => {
  const generateBtns = () =>
    shuffle([
      <Button
        key={uuid()}
        onClick={() => {
          handleCorrectAnswer();
          handlePlayNext();
        }}
      >
        {/* <img src={questions["correctAnswer"]} /> */}
        <Abcjs
          notation={notations[0]}
          parserParams={{}}
          // engraverParams={{ responsive: "resize" }}
          renderParams={{ viewportHorizontal: true }}
        />
      </Button>,
      <Button
        key={uuid()}
        onClick={() => {
          handleWrongAnswer();
          handlePlayNext();
        }}
      >
        {/* <img src={questions["wrongAnswer"]} /> */}
        <Abcjs
          notation={notations[1]}
          parserParams={{}}
          // engraverParams={{ responsive: "resize" }}
          renderParams={{ viewportHorizontal: true }}
        />
      </Button>
    ]);
  return (
    <div className="answers">
      <div className="answer-btns">{generateBtns().map(button => button)}</div>
    </div>
  );
};

const mapStateToProps = state => ({
  questions: state.questions
});

export default connect(mapStateToProps, {
  handleCorrectAnswer,
  handleWrongAnswer,
  handlePlayNext
})(Answers);
